import {
  Button,
  Card,
  CardActionArea,
  CardActions,
  CardContent,
  CardMedia,
  Typography,
} from "@mui/material";
import React from "react";
import { styled } from "@mui/material/styles";

const CardImage = styled("div")(({ theme }) => ({
  marginRight: theme.spacing(1),
  height: 250,
}));

const StyledCard = styled("div")(({ theme }) => ({
  marginBottom: theme.spacing(5),
}));

function Post() {
  return (
    <StyledCard>
      <Card>
        <CardActionArea>
          <CardImage>
            <CardMedia
              image="https://images.pexels.com/photos/12977998/pexels-photo-12977998.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"
              title="My Post"
              sx={{ height: 250 }}
            />
          </CardImage>
          <CardContent>
            <Typography gutterBottom variant="h5">
              My First Post
            </Typography>
            <Typography variant="body">
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Libero
              eaque ducimus maxime expedita, blanditiis explicabo recusandae
              nostrum provident sed accusantium ut repellendus dolore voluptatum
              consequuntur harum. Exercitationem porro provident nulla?
            </Typography>
          </CardContent>
        </CardActionArea>
        <CardActions>
          <Button size="small" color="primary">
            Share
          </Button>
          <Button size="small" color="primary">
            Learn More
          </Button>
        </CardActions>
      </Card>
    </StyledCard>
  );
}

export default Post;
